import React from 'react'
import { Form, Input, Button, Checkbox } from 'antd';
import axios from 'axios'
export default function MyForm() {
    const layout = {
        labelCol: {
          span: 8,
        },
        wrapperCol: {
          span: 16,
        },
    };
    const tailLayout = {
    wrapperCol: {
        offset: 8,
        span: 16,
    },
    };

    const onFinish = (values) => {
        console.log('Success:', values);
    };

    const onFinishFailed = (errorInfo) => {
        console.log('Failed:', errorInfo);
    };

    function checkName(rule, value, callback) {
      // debugger
      // if(!value) {
      //   callback()
      //   return
      // }
      // if(value.length === 6) {
      //   callback()

      // } else {
      //   callback('手机号格式错误')
      // }
      callback(phoneValidate(value))
    }
    const phoneValidate = async function(value) {
      debugger
      if(!value) return Promise.resolve()
      if (/^1[3456789]\d{9}$/.test(value)) return Promise.resolve();
      if(value) {
        await axios.get('https://cat.aidigger.com/api/v1/davinci_properties?cat_id=3').then(res => {
            debugger
                // console.log(res.status)
                console.log('333')
            })
      }
      return Promise.resolve('错误的手机号码');
    }

    return (
        <Form
            {...layout}
            name="basic"
            initialValues={{
                remember: true,
            }}
            onFinish={onFinish}
            onFinishFailed={onFinishFailed}
            >
        <Form.Item
            label="Username"
            name="username"
            rules={[
              {
                required: true,
                message: 'Please input your username!',
              },
              {
                validator: (rule, value, callback) => callback(phoneValidate(value))
              }
            ]}
        >
            <Input />
        </Form.Item>

        <Form.Item
            label="Password"
            name="password"
            rules={[
            {
                required: true,
                message: 'Please input your password!',
            },
            ]}
        >
            <Input.Password />
        </Form.Item>

        <Form.Item {...tailLayout}>
            <Button type="primary" htmlType="submit">
            Submit
            </Button>
        </Form.Item>
        </Form>
    )
}
