import React, { useState } from 'react'
import { Form, Input, Button, Checkbox } from 'antd';
import axios from 'axios'
import ReactDataSheet from 'react-datasheet'
import 'react-datasheet/lib/react-datasheet.css'
import './index.css'

export default function InfoChartHook() {
  // const value = {
  //   columns: [
  //     { title: '尺码表', key: uuid()},
  //     {title: '衣长（cm）', key: uuid()},
  //     {title: '肩宽（cm）', key: uuid()},
  //     {title: '胸围（cm）', key: uuid()},
  //     {title: '腰围（cm）', key: uuid()},
  //     {title: '袖长（cm）', key: uuid()}
  //   ],
  //   dataSource = [{}, {}, {}, {}, {}],
  //   tableTitle = ''
  // }
  // [{ value: 1, component: (
  //   <button onClick={() => {
  //     const _grid = grid
  //     _grid.splice(1,0,[{ value: 1 }, { value: 2 }])
  //     setGrig(_grid)
  //   }}>
  //     Rendered
  //   </button>
  // ) }, { value: 3 }],
  // [{ value: 2 }, { value: 4 }],

  const [grid, setGrig] = useState([
    [{ value: 1 }, { value: 3 }],
    [{ value: 2 }, { value: 4 }]
  ])
  const onCellsChanged = (changes) => changes.forEach(({cell, row, col, value}) => {
    debugger
    grid[row][col].value = parseInt(value)
    grid[row][col].expr = value
    console.log("New expression :" + value)
  })
  const MyViewComponent = () => {
    return (
      <div>123</div>
    )
  }
  const columns =  [
    { name: '尺码表'},
    {name: '衣长（cm）'},
    {name: '肩宽（cm）'},
    {name: '胸围（cm）'},
    {name: '腰围（cm）'},
    {name: '袖长（cm）'}
  ]
  return (
    <ReactDataSheet
      data={grid}
      valueRenderer={(cell) => {
        return cell.value
      }}
      onCellsChanged={changes => {
        changes.forEach(({ cell, row, col, value }) => {
          grid[row][col] = { ...grid[row][col], value }
        })
        setGrig(grid)
      }}
      // onCellsChanged={onCellsChanged}
      // attributesRenderer={(cell) => {'data-hint': cell.hint || {}}
      sheetRenderer={props => (
        <table className={props.className + ' my-awesome-extra-class'}>
            <thead>
                <tr>
                    <th className='action-cell' />
                    {columns.map(col => (<th>{col.name}</th>))}
                </tr>
            </thead>
            <tbody>
                {props.children}
            </tbody>
        </table>
      )}
      rowRenderer={props => (
        <tr>
            <td className='action-cell'>
                <input
                    type='checkbox'
                    checked={isSelected(props.row)}
                    onChange={selectHandler}
                />
            </td>
            {props.children}
        </tr>
      )}
      valueViewer={MyViewComponent}
      dataEditor={props => (
        props.col === 0 ? <MyDatePicker {...props} /> : <DataEditor {...props}/>
      )}
    />
  )
}
