import React, { useState } from 'react'
import { Select, Form, Input } from 'antd'

export default function ComplexSelect() {
  // const { values } = props3
  const values = [{ name: 'type1' }, { name: 'type2' }, { name: 'type3' }]
  const [selectedArr, setSelectedArr] = useState([])
  const [form] = Form.useForm()
  const { TextArea } = Input

  function onSelectChange(value) {
    // 获取子组件规则
    setSelectedArr(value)
    const obj = {}
    value.map(item => {
      obj[item.name] = item.name
    })
    debugger
    form.setFields(obj)
    console.log(value)
  }
  return (
    <div>
      <Select
        placeholder='请选择'
        onChange={e => onSelectChange(e)}
        // value={form.getFieldValue(config.key)}
        allowClear
        style={{ width: '100%' }}
        getPopupContainer={triggerNode => triggerNode.parentElement}
        mode="multiple"
      >
        {values.map(item => (
          <Select.Option key={item.name} value={item.name}>
            {item.name}
          </Select.Option>
        ))}
      </Select>
      {!!selectedArr.length && selectedArr.map((item, index) => {
        return (
          <div key={index}>
            <Form
              form={form}
              onValuesChange={() => {
                const values = form.getFieldsValue()
              }}
            >
              <table className='pm-table' style={{ border: 0 }}>
                <tr>
                  <td className='td1'>
                    <Form.Item name={item.name}>
                      <Input value={item.name} disabled />
                    </Form.Item>
                  </td>
                  <td>
                    <Form.Item name='描述'>
                      <TextArea placeholder={`请输入`} />
                    </Form.Item>
                  </td>
                  <td>
                    <Form.Item name='简介'>
                      <TextArea placeholder={`请输入`} />
                    </Form.Item>
                  </td>
                </tr>
              </table>
            </Form>
          </div>
        )})}
    </div>
  )
}
